using System;
using System.IO;
using System.Collections.Generic;
using Xunit;
using Xunit.Abstractions;
using MonoTests.Mono.Data.Sqlite;
using MonoTests.Mono.Data.Tds;
using MonoTests.System.Security.Cryptography;
using MonoTests.System.IO.Compression.FileSystem;
using MonoTests.System.IO.Compression;
using MonoTests.System;
using MonoTests.System.Numerics;
using MonoTests.System.Xml;
using MonoTests.System.ServiceModel;
using MonoTests.System.Transactions;
using MonoTests.System.Web.Services;


namespace Xamarin.iOS.UnitTests {
	// autogenerated class that uses types from the test assemblies so that
	// they are aot and not linked away.
	public static class RegisterType
	{
		public static bool IsXUnit = false;
		public static Dictionary <string, Type> TypesToRegister = new Dictionary <string, Type> {
			{ "xammac_Mono.Data.Sqlite_test.dll", typeof (MonoTests.Mono.Data.Sqlite.SqliteCommandUnitTests)}, 
			{ "xammac_Mono.Data.Tds_test.dll", typeof (MonoTests.Mono.Data.Tds.TdsConnectionPoolTest)}, 
			{ "xammac_Mono.Security_test.dll", typeof (MonoTests.System.Security.Cryptography.SHA224ManagedTest)}, 
			{ "xammac_System.IO.Compression.FileSystem_test.dll", typeof (MonoTests.System.IO.Compression.FileSystem.ZipArchiveTests)}, 
			{ "xammac_System.IO.Compression_test.dll", typeof (MonoTests.System.IO.Compression.ZipArchiveTests)}, 
			{ "xammac_System.Json_test.dll", typeof (MonoTests.System.JsonValueTests)}, 
			{ "xammac_System.Numerics_test.dll", typeof (MonoTests.System.Numerics.BigIntegerTest)}, 
			{ "xammac_System.Runtime.Serialization_test.dll", typeof (MonoTests.System.Xml.UniqueIdTest)}, 
			{ "xammac_System.ServiceModel.Web_test.dll", typeof (MonoTests.System.UriTemplateEquivalenceComparerTest)}, 
			{ "xammac_System.ServiceModel_test.dll", typeof (MonoTests.System.ServiceModel.FaultExceptionTest)}, 
			{ "xammac_System.Transactions_test.dll", typeof (MonoTests.System.Transactions.AsyncTest)}, 
			{ "xammac_System.Web.Services_test.dll", typeof (MonoTests.System.Web.Services.WebMethodAttributeTest)}, 
			{ "xammac_System.Xml.Linq_test.dll", typeof (MonoTests.System.Xml.ExtensionsTest)}, 

		};

		public static void RegisterTypes () 
		{
			// line used to ensure that the runner is not remove by the linker :/
#if MONOMAC
			Console.WriteLine ($"Got the runner for the linker {typeof(Xunit.Sdk.AllException)}");
#else
			Console.WriteLine ($"Got the runner for the linker {typeof(Xunit.Sdk.TypeUtility)}");
#endif
			foreach (var a in TypesToRegister.Keys) {
				// do something with the type, so that it is not removed
				var assemblyPath = Path.GetFileName (TypesToRegister[a].Assembly.Location);
				Console.WriteLine ($"We are using type {TypesToRegister[a]} from assembly {assemblyPath}");
			}
		}
	}
}